mapper.select:
SELECT order_info.order_id, car_info.car_number,car_info.brand_model,car_info.salesman, car_info.license_owner, order_info.create_time, date_format(order_info.finish_time,'%Y-%m-%d') as finish_time, order_info.create_by, order_info.pay_status, order_info.pay_type, order_info.pay_type_id, order_info.payment, order_info.pay_money, order_info.car_info_id, order_info.delivery_way, order_info.delivery_address, order_info.cancel_msg, order_info.update_time, order_info.contact_name, order_info.contact_tel, insured_info.posted_name, insured_info.insurance_company, quote_info.quote_insurance_name, quote_info.quote_source, quote_info.pay_end_date FROM order_info LEFT JOIN insured_info ON( order_info.car_info_id=insured_info.car_info_id) LEFT JOIN car_info ON (order_info.car_info_id=car_info.car_info_id) left join quote_info on (order_info.pay_type_id=quote_info.quote_id)
create_by=#{accountId}
and pay_status = #{payStatus}
ORDER BY order_info.create_time DESC

SELECT order_info.order_id, car_info.car_number,car_info.car_model,car_info.salesman, car_info.license_owner, order_info.create_time, date_format(order_info.finish_time,'%Y-%m-%d') as finish_time, order_info.create_by, order_info.pay_status, order_info.pay_type, order_info.payment, order_info.pay_money, order_info.car_info_id, order_info.delivery_way, order_info.delivery_address, order_info.contact_name, order_info.contact_tel, insured_info.posted_name, insured_info.insurance_company, FROM order_info LEFT JOIN insured_info ON( order_info.car_info_id=insured_info.car_info_id) LEFT JOIN car_info ON (order_info.car_info_id=car_info.car_info_id)
create_by=#{accountId}
and pay_status = #{payStatus}
and order_info.car_info_id=#{carInfoId}
and insured_info.posted_name=#{postedName}
and order_info.delivery_way=#{deliveryWay}
and insured_info.insurance_company=#{insuranceCompany}
ORDER BY order_info.create_time DESC
