mapper.select:
SELECT order_info.order_id, car_info.car_number,car_info.car_model,car_info.salesman, date_format(order_info.create_time,'%Y-%m-%d') as create_time, date_format(order_info.finish_time,'%Y-%m-%d') as finish_time, order_info.account_id, order_info.pay_status, order_info.pay_type, order_info.payment, order_info.pay_money, order_info.car_info_id, order_info.delivery_way, order_info.delivery_address, order_info.contact_name, order_info.contact_tel, insured_info.posted_name, insured_info.insurance_company FROM order_info LEFT JOIN insured_info ON( order_info.car_info_id=insured_info.car_info_id) LEFT JOIN car_info ON (order_info.car_info_id=car_info.car_info_id)
account_id=#{accountId}
and pay_status = #{payStatus}

SELECT order_info.order_id, car_info.car_number,car_info.car_model,car_info.salesman, date_format(order_info.create_time,'%Y-%m-%d') as create_time, date_format(order_info.finish_time,'%Y-%m-%d') as finish_time, order_info.account_id, order_info.pay_status, order_info.pay_type, order_info.payment, order_info.pay_money, order_info.car_info_id, order_info.delivery_way, order_info.delivery_address, order_info.contact_name, order_info.contact_tel, insured_info.posted_name, insured_info.insurance_company, insured_info.posted_name, insured_info.insurance_company FROM order_info LEFT JOIN insured_info ON( order_info.car_info_id=insured_info.car_info_id) LEFT JOIN car_info ON (order_info.car_info_id=car_info.car_info_id)
account_id=#{accountId}
and pay_status = #{payStatus}
and order_info.car_info_id=#{carInfoId}
and insured_info.posted_name=#{postedName}
and order_info.delivery_way=#{deliveryWay}
and insured_info.insurance_company=#{insuranceCompany}
